import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

const Login = () => {
    const [formData, setFormData] = useState({ email: "", password: "" });
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        try {
            const response = await axios.post('https://report-management-system-backend.onrender.com/login', formData);
            const { access_token, role } = response.data;
            
            // Store token and user info
            localStorage.setItem('authToken', access_token);
            const decoded = jwtDecode(access_token);
            localStorage.setItem('userRole', role);
            localStorage.setItem('userId', decoded.id);
            localStorage.setItem('department', response.data.department);

            // Redirect based on role
            navigate(role === 'head_of_planning' ? '/reports' : '/dashboard');
        } catch (error) {
            setError(error.response?.data?.error || "Login failed. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-secondary">
          <div className="card w-[450px] p-8 shadow-lg rounded-lg bg-white">
            {/* Logo */}
            <div className="flex justify-center mb-4">
              <img src="data:image/png;base64," alt="Logo" className="h-16 w-auto" />
            </div>
      
            <h1 className="text-3xl font-bold mb-6 text-center">Login</h1>
      
            {error && <div className="mb-4 text-red-500 text-center">{error}</div>}
      
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label className="block text-gray-700 mb-2">Email:</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full p-3 border rounded text-lg"
                  required
                />
              </div>
      
              <div className="mb-6">
                <label className="block text-gray-700 mb-2">Password:</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className="w-full p-3 border rounded text-lg"
                  required
                />
              </div>
      
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-primary text-white py-3 px-4 rounded text-lg hover:bg-blue-600 transition-colors"
              >
                {loading ? 'Logging in...' : 'Login'}
              </button>
            </form>
          </div>
        </div>
      );
      
};

export default Login;